import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NewsCard from './NewsCard';

const NewsList = ({ country }) => {
    const [articles, setArticles] = useState([]);
    // setArticles = updates the values everytime we fetch data 

    useEffect(() => {
        const getArticles = async () => { // Use % as a space within the query
            const response = await axios.get("https://newsapi.org/v2/everything?q=driveworks&apiKey=f1d7f556f47a417591052b958867f88c");
            console.log(response);
            setArticles(response.data.articles);
        }
        getArticles();
    }, [])


    return (
        <div>
            {articles.map(article => {
                return (
                    <NewsCard
                        // country={country}
                        // title={article.title}
                        // description={article.description}
                        // url={article.url}
                        // urlToImage={article.urlToImage}
                        country = {country }
                    />
                )
            })}
        </div>
    )
}

export default NewsList
